targets.expr:
sum by (instance) (
  irate(node_network_transmit_bytes_total{
    job="$job", device=~"eth.*|ens.*|eno.*|enp.*|enx.*|enX.*|wlan.*|venet.*"
  }[$__rate_interval])
  or
  irate(windows_net_bytes_sent_total{
    job="$job"
  }[$__rate_interval])
) * 8

node_memory_MemTotal_bytes{instance="$node", job="$job"} - node_memory_MemFree_bytes{instance="$node", job="$job"} - (node_memory_Cached_bytes{instance="$node", job="$job"} + node_memory_Buffers_bytes{instance="$node", job="$job"} + node_memory_SReclaimable_bytes{instance="$node", job="$job"})
or
windows_os_visible_memory_bytes{instance="$node", job="$job"} - windows_os_physical_memory_free_bytes{instance="$node", job="$job"}

(
  node_filesystem_size_bytes{
    job="$job", instance="$node",
    fstype=~"ext.|xfs|nfs", mountpoint!~".*pod.*"
  }
)
or
(
  label_replace(
    label_replace(
      label_replace(
        windows_logical_disk_size_bytes{job="$job", instance="$node"},
        "device", "$1", "volume", "(.*)"
      ),
      "mountpoint", "$1", "volume", "(.*)"
    ),
    "fstype", "ntfs", "volume", ".*"
  )
)

(
  node_filesystem_free_bytes{
    job="$job", instance="$node",
    fstype=~"ext.|xfs|nfs", mountpoint!~".*pod.*"
  }
)
or
(
  label_replace(
    label_replace(
      label_replace(
        windows_logical_disk_free_bytes{job="$job", instance="$node"},
        "device", "$1", "volume", "(.*)"
      ),
      "mountpoint", "$1", "volume", "(.*)"
    ),
    "fstype", "ntfs", "volume", ".*"
  )
)

(
  (node_filesystem_size_bytes{job="$job", instance="$node", fstype=~"ext.|xfs|nfs", mountpoint!~".*pod.*"}
   - node_filesystem_free_bytes{job="$job", instance="$node", fstype=~"ext.|xfs|nfs", mountpoint!~".*pod.*"})
  * 100
  / node_filesystem_size_bytes{job="$job", instance="$node", fstype=~"ext.|xfs|nfs", mountpoint!~".*pod.*"}
)
or
(
  (
    label_replace(label_replace(windows_logical_disk_size_bytes{job="$job", instance="$node"}, "device","$1","volume","(.*)"), "mountpoint","$1","volume","(.*)")
    - label_replace(label_replace(windows_logical_disk_free_bytes{job="$job", instance="$node"}, "device","$1","volume","(.*)"), "mountpoint","$1","volume","(.*)")
  )
  * 100
  / label_replace(label_replace(windows_logical_disk_size_bytes{job="$job", instance="$node"}, "device","$1","volume","(.*)"), "mountpoint","$1","volume","(.*)")
)
